import logo from "./logo.svg";
import "./App.css";
import TopPreview from "./components/topPreview/topPreview";
import SearchBox from "./components/searchBare/component";
import { useEffect, useState } from "react";
import urls from "./envirment/envirment";
import axios from "axios";
import EachItem from "./components/eachItem/component";
function App() {
  const [select, setSelect] = useState();
  const [categories, setCategories] = useState([]);
  const [drinksList, setDrinksList] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    cockTailCategories();
  }, []);

  // function to get cocktailList
  function cockTailCategories() {
    setCategories([]);
    setLoading(true);
    axios
      .get(urls.cocktail.list)
      .then((e) => {
        // console.log(e.data.drinks[0]);
        setSelect(e.data?.drinks[0] || "");
        setCategories(e.data?.drinks || []);
        // get each Categories data //1st time
        showSelectedCocktails(e.data?.drinks[0].strCategory || "");
        setLoading(false);
      })
      .catch((e) => {
        console.log(e);
        setLoading(false);
      });
  }

  function showSelectedCocktails(category = "") {
    category = category.toString().replace(/ /g, "_");
    category = category.trim();
    if (!category) return;
    setDrinksList([]);
    setLoading(true);
    axios
      .get(urls.cocktail.category_list + category)
      .then((e) => {
        console.log(e.data?.drinks);
        setDrinksList(e.data?.drinks);
        setLoading(false);
      })
      .catch((e) => {
        console.log(e);
        setLoading(false);
      });
  }

  return (
    <div className="App">
      {loading ? <LoadingView /> : ""}

      <TopPreview />
      <div className="container mt-5">
        <SearchBox />

        <div className="row mt-5">
          <div className="col-md-4 mb-5">
            <ul className="list-group">
              {categories.map((e, i) => {
                return (
                  <li
                    onClick={() => {
                      showSelectedCocktails(e.strCategory);
                    }}
                    key={i}
                    className={
                      e.strCategory == select.strCategory
                        ? "list-group-item active"
                        : "list-group-item"
                    }
                  >
                    {e.strCategory}
                  </li>
                );
              })}
            </ul>
          </div>
          <div className="col-md-8">
            <div className="eachElement">
              {drinksList.map((e, i) => {
                return <EachItem key={i} data={e} />;
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function LoadingView() {
  return (
    <div className="loadinganimationðŸ”ƒ">
      <lottie-player
        style={{ height: "150px", width: "150px" }}
        src="https://assets1.lottiefiles.com/packages/lf20_4he3dgtd.json"
        background="transparent"
        speed="0.5"
        loop
        autoplay
      ></lottie-player>
    </div>
  );
}

export default App;
