import axios from "axios";
import { useEffect, useState } from "react";
import urls from "../../envirment/envirment";
import "./style.css";

export default function ViewFullDetails({ popUpData, setPopUpData }) {
  const [details, setDetails] = useState(null);
  const [Ingredient, setIngredient] = useState(null);
  const [Measure, setMeasure] = useState(null);
  // function for fatchData
  function getCocktelDetails(id) {
    setDetails(null);
    axios
      .get(urls.cocktail.fullDetailse + id)
      .then((o) => {
        console.log(o.data?.drinks[0] || null);
        setDetails(o.data?.drinks[0] || null);
        IngredientArr(o.data?.drinks[0] || null);
        MeasureArr(o.data?.drinks[0] || null);
      })
      .catch((e) => {
        console.log(e);
        alert(e.message || "Something went wrong!");
      });
  }
  useEffect(() => {
    getCocktelDetails(popUpData);
  }, []);

  // set Ingredient fn
  function IngredientArr(obj = null) {
    if (!obj) return null;
    const temp = [];
    for (let e in obj) {
      if (/^strIngredient/.test(e) && obj[e])
        temp.push({ name: e, value: obj[e] });
    }
    console.log(temp);
    return temp;
  }

  // set Measure fn
  function MeasureArr(obj = null) {
    if (!obj) return null;
    const temp = [];
    for (let e in obj) {
      if (/^strMeasure/.test(e) && obj[e])
        temp.push({ name: e, value: obj[e] });
    }
    console.log(temp);
    return temp;
  }

  /*
  🍷 main container box
  */
  return (
    <div className="🤪viewFullDetails">
      <div className="cardContainer">
        <div className="topNavBar">
          <div className="title">{details?.strDrink || "😅 Loading..."}</div>
          <div
            className="close"
            onClick={() => {
              setPopUpData(null);
            }}
          >
            <i className="icofont-close-line"></i>
          </div>
        </div>

        <div className="popupBody">
          {!details ? <Loading /> : <CocktailDetails details={details} />}
        </div>
      </div>
      <div
        className="bgTap"
        onClick={() => {
          setPopUpData(null);
        }}
      ></div>
    </div>
  );
}

/*
  🔃 Loading box here
  */

const Loading = () => {
  return (
    <div className="loadingInPopup">
      <lottie-player
        style={{ height: "250px", width: "250px" }}
        src="./assets/cocktail.json"
        background="transparent"
        speed="10"
        loop
        autoplay
      ></lottie-player>
    </div>
  );
};

/*
  💁 Loading box here
*/

const CocktailDetails = ({ details }) => {
  return (
    <div className="🍷cocktailDetails">
      <div className="session1">
        <img src={details?.strDrinkThumb} alt="" />
        <div className="title">
          <b>{details?.strDrink || "Unavalable"}</b>
        </div>
        <div className="subtitle">
          {details?.strAlcoholic || "Non Alcoholic"}
        </div>
      </div>

      <div className="session2">
        <table class="table table-borderless">
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td colspan="2">Mark</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};
