import EachItem from "../eachItem/component";
import "./style.css";

function DefaultContainerView({ categories, drinksList, showPopUp, select, showSelectedCocktails }) {
    return <div className="outer_div">
        <div className="w-100">
            <div className="row mt-5">
                <div className="col-md-4 mb-5">
                    <div className="scroll-content-left">
                        <ul className="list-group w-100">
                            {categories.map((e, i) => {
                                return (
                                    <li
                                        onClick={() => {
                                            showSelectedCocktails(e.strCategory);
                                        }}
                                        key={i}
                                        className={
                                            e.strCategory == select
                                                ? "list-group-item active"
                                                : "list-group-item"
                                        }
                                    >
                                        {e.strCategory}
                                    </li>
                                );
                            })}
                        </ul>
                    </div>
                </div>
                <div className="col-md-8">
                    <div className="eachElement">
                        {drinksList.map((e, i) => {
                            return (
                                <div
                                    key={i}
                                    onClick={() => {
                                        showPopUp(e?.idDrink);
                                    }}
                                >
                                    <EachItem data={e} />
                                </div>
                            );
                        })}
                    </div>
                </div>
            </div>
        </div>
    </div>
}

export default DefaultContainerView
