import logo from "./logo.svg";
import "./App.css";
import TopPreview from "./components/topPreview/topPreview";
import SearchBox from "./components/searchBare/component";
import { useState } from "react";
import urls from "./envirment/envirment";
import axios from "axios";
function App() {
  const [select, setSelect] = useState();
  const [categories, setCategories] = useState([]);

  // function to get cocktailList
  function cockTailCategories() {
    axios.get(urls.cocktail.list).then((e) => {
      setCategories(e.drinks || []);
    });
  }

  cockTailCategories();

  return (
    <div className="App">
      <TopPreview />
      <div className="container mt-5">
        <SearchBox />

        <div className="row mt-5">
          <div className="col-md-4">
            <ul className="list-group">
              {categories.map((e) => {
                return (
                  <li className="list-group-item active" aria-current={"true"}>
                    {e.strCategory}
                  </li>
                );
              })}
            </ul>
          </div>
          <div className="col-md-8"></div>
        </div>
      </div>
    </div>
  );
}

export default App;
